import React, {createContext, useState, useEffect} from "react";
import all_product from "../Components/Assets/all_product";

export const ShopContext = createContext(null);

const ShopContextProvider = (props) => {

    const [products, setProducts] = useState([]);
    useEffect(() => {
        // Your logic to fetch product data and update products state
        setProducts(fetchedProducts); // Replace with actual data
    }, []);
    const contextValue = { all_product: products };
    console.log("all_product data in ShopContext:", contextValue.all_product);

    return (
        <ShopContext.Provider value={contextValue}>
            {props.children}
        </ShopContext.Provider>
    )
}
export default ShopContextProvider;
