import React, { useContext, useState } from "react";
import '../ProductDisplay/ProductDisplay.css';
import { ShopContext } from "../../Context/ShopContext";


const ProductDisplay = (props) => {
      const {product} = props;
      const {addToCart} = useContext(ShopContext);
      const [selectedSize, setSlectedSize] = useState("sizeS");

      const addToCartHandler = () => {
        addToCart(product.id, selectedSize);
      }


    return (
        <div className="productdisplay">
            <div className="productdisplay-left">
                <div className="productdisplay-img">
                    <img className="productdisplay-main-img" src={product.image} alt="" />
                </div>
            </div>
            <div className="productdisplay-right">
              <div className="productdisplay-right-size">
                <h1>Select size</h1>
                <div className="productdisplay-right-sizes">
                    {["sizeS","sizeM","sizeL"].map((size)=>(
                        <div 
                            key={size}
                            className={`productdisplay-size ${size === selectedSize ? "active" : ""}`}
                            onClick={()=> setSlectedSize(size)}
                        >
                            {size}
                        </div>
                    ))}
                </div>
              </div>
              <button onClick={()=>{addToCartHandler(product.id)}}>ADD TO CART</button>
            </div>
            
            
        </div>
    )
}
export default ProductDisplay;
